<?php#region License Statement	// Copyright (c) SeniorPartners. All rights reserved.	//	// 클래스명	: EZ_Contime	// 경로		: [framework]/_include/class/data/	// 코드		: ANSI	// 설명		: 접속자 수 모듈 클래스	// 작성자	: 김성호 (sh.kim@yourstage.com)	// 소속명	: Development Team	// 수정일	: 20090325#endregionif( !class_exists("EZ_Contime") ){	require_once "class.BaseModule.php";	class EZ_Contime extends BaseModule	{		//클래스 생성자		function EZ_Contime()		{			parent::BaseModule();			$this->InitField( "ez_contime" );		//접속자 수정보			$this->InitListInfo( "ez_contime" );	//접속자 수정보 List 관리용 변수 초기화		}		#region Base Statement			/**			* 데이터 초기화			*			* @param string		$as_TableName			:	테이블명			*/			function InitField( $as_TableName )			{				switch( $as_TableName )				{					case "ez_contime":						$this->Fields[$as_TableName]	= Array(															'time'		=> Array('ISFIELD'=>true, 'USED'=>false, 'TYPE'=>"VALUE", 'DATA'=>0),															'cnt'		=> Array('ISFIELD'=>true, 'USED'=>true, 'TYPE'=>"VALUE", 'DATA'=>0)															);						break;					default:						parent::InitField( $as_TableName );						break;				}			}			/**			* 레코드 삽입			*			* @param string		$as_TableName			:	테이블명			* @return boolean			*/			function Insert( $as_TableName )			{				switch( $as_TableName )				{					case "ez_contime":	//특정필드 데이터 설정						$this->SetData( $as_TableName, "time", date( "YmdH" ) );						$this->SetData( $as_TableName, "cnt", 1 );						break;					default:						break;				}				return parent::Insert( $as_TableName );			}			/**			* 레코드 반영			*			* @param string		$as_TableName			:	테이블명			* @param string		$as_Condition			:	쿼리 조건부			* @return boolean			*/			function Update( $as_TableName, $as_Condition )			{				switch( $as_TableName )				{					case "ez_contime":	//키값 및 기타 데이터 변경방지						$this->SetUsable( $as_TableName, 'time', false );						break;					default:						break;				}				return parent::Update( $as_TableName, $as_Condition );			}			/**			* 레코드 삭제			*			* @param string		$as_TableName			:	테이블명			* @param string		$as_Condition			:	쿼리 조건부			* @return boolean			*/			function Del( $as_TableName, $as_Condition )			{				return parent::Del( $as_TableName, $as_Condition );			}		#endregion	}}?>