<?php#region License Statement	// Copyright (c) SeniorPartners. All rights reserved.	//	// 클래스명	: EZ_Mailing	// 경로		: [framework]/_include/class/data/	// 코드		: ANSI	// 설명		: 메일링 모듈 클래스	// 작성자	: 김성호 (sh.kim@yourstage.com)	// 소속명	: Development Team	// 수정일	: 20090325#endregionif( !class_exists("EZ_Mailing") ){	require_once "class.BaseModule.php";	class EZ_Mailing extends BaseModule	{		//클래스 생성자		function EZ_Mailing()		{			parent::BaseModule();			$this->InitField( "ez_mailing" );		//메일링정보			$this->InitListInfo( "ez_mailing" );	//메일링정보 List 관리용 변수 초기화		}		#region Base Statement			/**			* 데이터 초기화			*			* @param string		$as_TableName			:	테이블명			*/			function InitField( $as_TableName )			{				switch( $as_TableName )				{					case "ez_mailing":						$this->Fields[$as_TableName]	= Array(															'idx'		=> Array('ISFIELD'=>true, 'USED'=>false, 'TYPE'=>"VALUE", 'DATA'=>0),															'wdate'		=> Array('ISFIELD'=>true, 'USED'=>true, 'TYPE'=>"VALUE", 'DATA'=>""),															'name'		=> Array('ISFIELD'=>true, 'USED'=>true, 'TYPE'=>"VALUE", 'DATA'=>""),															'address'	=> Array('ISFIELD'=>true, 'USED'=>true, 'TYPE'=>"VALUE", 'DATA'=>""),															'email'		=> Array('ISFIELD'=>true, 'USED'=>true, 'TYPE'=>"VALUE", 'DATA'=>"")															);						break;					default:						parent::InitField( $as_TableName );						break;				}			}			/**			* 레코드 삽입			*			* @param string		$as_TableName			:	테이블명			* @return boolean			*/			function Insert( $as_TableName )			{				switch( $as_TableName )				{					case "ez_mailing":	//특정필드 데이터 설정						$this->SetUsable( $as_TableName, 'idx', false );						$this->SetData( $as_TableName, "wdate", "now()", "METHOD" );						break;					default:						break;				}				return parent::Insert( $as_TableName );			}			/**			* 레코드 반영			*			* @param string		$as_TableName			:	테이블명			* @param string		$as_Condition			:	쿼리 조건부			* @return boolean			*/			function Update( $as_TableName, $as_Condition )			{				switch( $as_TableName )				{					case "ez_mailing":	//키값 및 기타 데이터 변경방지						$this->SetUsable( $as_TableName, 'idx', false );						$this->SetUsable( $as_TableName, "wdate", false );						break;					default:						break;				}				return parent::Update( $as_TableName, $as_Condition );			}			/**			* 레코드 삭제			*			* @param string		$as_TableName			:	테이블명			* @param string		$as_Condition			:	쿼리 조건부			* @return boolean			*/			function Del( $as_TableName, $as_Condition )			{				return parent::Del( $as_TableName, $as_Condition );			}		#endregion	}}?>