<?php#region License Statement	// Copyright (c) SeniorPartners. All rights reserved.	//	// 클래스명	: UserLoginModule	// 경로		: [www]/_include/class/data/	// 코드		: ANSI	// 설명		: 사용자 Login 모듈 클래스	// 작성자	: 김성호 (sh.kim@yourstage.com)	// 소속명	: Development Team	// 수정일	: 20090318#endregionif( !class_exists("UserLoginModule") ){	global $gcls_ClassCollection;	$gcls_ClassCollection->LoadClassDefine( "/class/data", "EZ_Member", GDC_INC_ROOT );	require_once "class.SolutionBaseModule.php";	class UserLoginModule extends SolutionBaseModule	{		var $cls_EZ_MemberControl	= null;	//고객정보 관리용 클래스		//클래스 생성자		function UserLoginModule()		{			$this->InitField();		}		#region 상속받은 메소드 보완			/**			* 데이터 초기화			*			*/			function InitField()			{				parent::InitField();				$this->cls_EZ_MemberControl = new EZ_Member();			}		#endregion		#region DB 연동 클래스 관련 셋팅 처리			/**			* DBA 셋팅			*			* @param class		$acls_DBA				:	DBA 클래스			*/			function SetDBA( $acls_DBA )			{				$this->cls_EZ_MemberControl->SetDBA( $acls_DBA );			}		#endregion		#region EZ_Member 영역			/**			* EZ_Member 특정 필드 데이터 획득			*			* @param string		$as_FieldName			:	필드명			* @return string			*/			function GetDataOfEZMember( $as_FieldName )			{				return $this->cls_EZ_MemberControl->GetData( "ez_member", $as_FieldName );			}			/**			* 로그인 체크			*			* @param string		$as_ID			:	아이디			* @param string		$as_PWD			:	패스워드			* @return boolean			*/			function IsMember( $as_ID, $as_PWD )			{				$this->cls_EZ_MemberControl->Load( "ez_member", "WHERE (id='" . $as_ID . "') and (passwd='" . $as_PWD . "')" );				if( $this->cls_EZ_MemberControl->GetData( "ez_member", "id" ) )				{					return true;				}				else				{					return false;				}			}			/**			* 고객 존재여부 체크			*			* @param string		$as_ID			:	아이디			* @return boolean			*/			function IsExistMember( $as_ID )			{				$this->cls_EZ_MemberControl->Load( "ez_member", "WHERE (id='" . $as_ID . "')" );				if( $this->cls_EZ_MemberControl->GetData( "ez_member", "id" ) )				{					return true;				}				else				{					return false;				}			}			/**			* 고객 존재여부 체크			*			* @param string		$as_Resno		:	주민등록번호			* @return boolean			*/			function IsExistResno( $as_Resno )			{				$this->cls_EZ_MemberControl->Load( "ez_member", "WHERE (resno='" . $as_Resno . "')" );				if( $this->cls_EZ_MemberControl->GetData( "ez_member", "id" ) )				{					return true;				}				else				{					return false;				}			}			/**			* 아이디/패스워드 찾기			*			* @param string		$as_Name		:	이름			* @param string		$as_Email		:	이메일			* @param string		$as_Resno		:	주민등록번호			* @return boolean			*/			function FindMemberID( $as_Name, $as_Email, $as_Resno )			{				$this->cls_EZ_MemberControl->Load( "ez_member", "WHERE (`name`='" . $as_Name . "') and (email='" . $as_Email . "') and (resno='" . $as_Resno . "')" );				if( $this->cls_EZ_MemberControl->GetData( "ez_member", "id" ) )				{					return true;				}				else				{					return false;				}			}		#endregion		#region Field Element Print 영역			/**			* 입력/수정용 split 출력			*			* @param string		$as_Field			:	data 구분			* @param integer	$ai_Index			:	part 구분			* @return string			*/			function PrintSeperateOfData( $as_Field, $ai_Index )			{				$iarr_data = explode("-", $this->GetDataOfEZMember( $as_Field ) );				return ( $ai_Index > (count($iarr_data)-1) ) ? "" : $iarr_data[$ai_Index];			}		#endregion	}}?>