<?php#region License Statement	// Copyright (c) Sitemaps.co.kr All rights reserved.	//	// Filename	: Tag.class.php	// Path		: [framework]/System/MarkupLanguage/	// Code		: UTF-8	// Desc		: Tag	// Author	: Sergei Kim (김성호, master@newstools.kr)	// Update	: 20091117#endregionif(!defined('__FRAMEWORK_PATH__')){header("HTTP/1.1 404 Not Found");	header("Content-Type: text/html; charset=utf-8");	exit();}if( !class_exists("Tag") ){	Packager::LoadNamespace( "System" );	class Tag extends SystemClass	{		#region Fields Statement			//protected function SiteURL($case, $value=null){		global $gcls_GlobalConfiguration; switch( strtolower( $case ) ) { case 'get':	return $gcls_GlobalConfiguration->GetAppSetting('SiteURL');		break; } }			//public $UserType	= "";		#endregion		#region CreateTag Area			/// <summary>			/// CreateTag : Tag 생성/출력			/// </summary>			/// <param name="tagSeparator">태그구분명칭</param>			/// <param name="isClosed">태그 마침여부</param>			/// <param name="tagID">태그ID</param>			/// <param name="tagName">태그명</param>			/// <param name="option">옵션</param>			/// <returns>출력물</returns>			public static function CreateTag($tagSeparator, $isClosed, $tagID='', $tagName='', $option='')			{				$rtn = "";				$rtn = "<" . $tagSeparator;				$rtn.= empty($tagID) ? "" : sprintf(" id=\"%s\"", $tagID);				$rtn.= empty($tagName) ? "" : sprintf(" name=\"%s\"", $tagName);				$rtn.= empty($option) ? "" : sprintf(" %s", $option);				$rtn.= $isClosed ? " /" : "";				$rtn.= ">";				return $rtn;			}			/// <summary>			/// CreateTagBlock : TagBlock 생성/출력			/// </summary>			/// <param name="tagSeparator">태그구분명칭</param>			/// <param name="content">태그 영역 내부내용</param>			/// <param name="tagID">태그ID</param>			/// <param name="tagName">태그명</param>			/// <param name="option">옵션</param>			/// <returns>출력물</returns>			public static function CreateTagBlock($tagSeparator, $content, $tagID='', $tagName='', $option='')			{				$rtn = "";				$rtn = Tag::CreateTag($tagSeparator, false, $tagID, $tagName, $option);				$rtn.= $content;				$rtn.= "</" . $tagSeparator . ">";				return $rtn;			}		#endregion	}}?>